var
  n,m,k,i,j,k1,ans,t,ans1,num:longint;
  a:array[1..1000] of longint;
  b,sum:array[0..20] of longint;
  f:array[1..20,1..20] of longint;
  x,l,r,mid:longint;

function min(x,y:longint):longint;
begin
  if x<y then exit(x);
  exit(y);
end;

procedure swap(var x,y:longint);
var t:longint;
begin
  t:=x;
  x:=y;
  y:=t;
end;


procedure qsort(l,r:longint);
var
  i,j,mid,mida:longint;
begin
  i:=l; j:=r; mid:=(i+j)div 2;
  mida:=a[mid];
  repeat
    while a[i]<mida do inc(i);
    while a[j]>mida do dec(j);
    if i<=j then
    begin
      if i<j then
      begin
        swap(a[i],a[j]);
      end;
      inc(i); dec(j);
    end;
  until i>j;
  if i<r then qsort(i,r);
  if l<j then qsort(l,j);
end;

begin
  readln(n,m,k);
  if m=1 then
  begin
   write(0);
   halt;
  end;
  for i:=1 to n do
   read(a[i]);

  qsort(1,n);

  for i:=1 to k do
  begin
   for j:=1 to m do
   begin
    read(num);
    b[num]:=a[j+(i-1)*(m-1)];
   end;
   for j:=1 to m-1 do
    b[j+m]:=b[j];

   for j:=1 to 2*m-1 do
    sum[j]:=sum[j-1]+b[j];

   for t:=2 to m do
    for j:=1 to 2*m-t do
    begin
     f[j,j+t-1]:=maxlongint;
     for k1:=j to j+t-2 do
      f[j,j+t-1]:=min(f[j,j+t-1],f[j,k1]+f[k1+1,j+t-1]);
     if f[j,j+t-1]=maxlongint then f[j,j+t-1]:=0;
     inc(f[j,j+t-1],sum[j+t-1]-sum[j-1]);
    end;

   ans1:=f[1,m];
   for j:=2 to m do
    ans1:=min(ans1,f[j,j+m-1]);
   inc(ans,ans1);

   x:=sum[j];
   l:=i*(m-1)+2;
   if x<a[l] then a[l-1]:=x
   else
   begin
    r:=n;
    while l+1<r do
    begin
     mid:=(l+r) div 2;
     if a[mid]>=x then r:=mid
     else l:=mid;
    end;
    for j:=i*(m-1)+2 to l do
     a[j-1]:=a[j];
    a[l]:=x;
   end;
  end;

  writeln(ans);
end.

