                else sum_1+=28*86400;
            }
            if(sum_1>mlt)break;
        }
        for(j=1;j<mm;j++)
            if(j!=2)mlt-=mouth[j]*86400;
            else
            {
                if(is_ok(yy))mlt-=29*86400;
                else mlt-=28*86400;
            }
        dd=mlt/86400+1;//确定天数
        mlt%=86400;
        hh=mlt/3600;//确定小时
        mlt%=3600;
        minn=mlt/60;//确定分
        mlt%=60;
        secc=mlt;//确定秒
