#include<bits/stdc++.h>
using namespace std;
int n,ans;
void sousuo(int z,int sum){//z 牧草 sum 枚举到第几块
	if(sum==4&&z==0){
		ans++;
		return ;
	}
	else if(sum>=4&&z!=0) {
		return;
	}
	else {
		if(sum==3){//剪枝
			float q=sqrt(z);
			if(q==int(q)){
				ans++;
			}
			return;
		}
		for(int i=0;i<=sqrt(n);i++){
			if(z-pow(i,2)>=0)
			sousuo(z-pow(i,2),++sum);
			--sum;
		}
	}
}
int main()
{
	cin>>n;
	sousuo(n,0);
	cout<<ans;
	return 0;
}
