#define rs u<<1|1
using namespace std;
int n,T,cnt[50],ans=25,x,y;
inline int read(){
	int k=1,x=0;
	char ch=getchar();
	while(!isdigit(ch)) {if(ch=='-')k=-1*k;ch=getchar();}
	while(isdigit(ch)) x=(x<<1)+(x<<3)+(ch^48),ch=getchar();
	return x*k;
}
void DFS(int out){
	if(out>=ans) return;
	int opo=0;
	for(int i=3;i<=10;++i){
		opo=1;
		if(!cnt[i]) continue;
		for(int j=i+1;j<=15;++j){
			if(opo>=5){
				for(int k=i;k<j;++k) --cnt[k];
				DFS(out+1);
				for(int k=i;k<j;++k) ++cnt[k];
			}
			if(!cnt[j]) break;++opo;
		}
	} //单顺
	for(int i=3;i<=12;++i){
		opo=1;
		if(cnt[i]<2) continue;
		for(int j=i+1;j<=15;++j){
			if(opo>=3){
				for(int k=i;k<j;++k) cnt[k]-=2;
				DFS(out+1);
				for(int k=i;k<j;++k) cnt[k]+=2;
			}
			if(cnt[j]<2) break;++opo;
		}		
	}//双顺
	for(int i=3;i<=13;++i){
		opo=1;
		if(cnt[i]<3) continue;
		for(int j=i+1;j<=15;++j){
			if(opo>=2){
				for(int k=i;k<j;++k) cnt[k]-=3;
				DFS(out+1);
				for(int k=i;k<j;++k) cnt[k]+=3;
			}
			if(cnt[j]<3) break;++opo;
		}
	}//三顺
	int a[5]={0,0,0,0,0};
	for(int i=2;i<=15;++i)
		if(cnt[i]<=4) a[cnt[i]]++;
		else{
			a[0]=cnt[i];
			while(a[0]>4) a[4]++,a[0]-=4;
			a[a[0]]++;
		}
	a[0]=a[1]+a[2]+a[3]+a[4];
	for(int i=1;i<=a[4];++i)
		if(a[2]>=2) a[2]-=2,a[0]-=2;
		else if(a[1]>=2) a[1]-=2,a[0]-=2;
		else break;
	for(int i=1;i<=a[3];++i)
		if(a[2]) --a[2],a[0]-=1;
		else if(a[1]) --a[1],a[0]-=1;
		else break;
	ans=min(ans,a[0]+out);

}
