        }
    }//单顺子
    for (k=3;k<=12;k++)
    {
        l=0;
        for (m=k;m<=14;m++){
            if (f.l[m]>1) l++; else break;
            if (l>=3){
                for (h=k;h<=m;h++) f.l[h]-=2;
                make(f,step+1);
                f=s;
            }
        }
    }//双顺子
    for (k=3;k<=13;k++)
    {
        l=0;
        for (m=k;m<=14;m++)
        {
            if (f.l[m]>2) l++; else break;
            if (l>=2){
                for (h=k;h<=m;h++)
                    f.l[h]-=3;
                make(f,step+1);
                f=s;
            }
        }
    }//三顺子
    for (k=3;k<=15;k++){
        if (f.l[k]>2){
            f.l[k]-=3;
            f.ld++;
            make(f,step+1);
            f=s;
        }
    }//三张
    for (k=3;k<=15;k++){
        if (f.l[k]>3){
            f.l[k]-=4;
            f.zd++;
            make(f,step+1);
            f=s;
        }
    }//炸弹
    int dan=0,dui=0;
    for (k=3;k<=15;k++)
    switch(f.l[k]){
        case 1:dan++;break;
        case 2:dui++;break;
        case 3:dan++;dui++;break;
        case 4:dui+=2;break;
    }//整理散牌
    if (f.w) dan+=f.w;
    while (f.zd>0 && (dan>1||dui>0)){
        if (dui>1) dui-=2;
        else if (dan>1) dan-=2;
        else if (dui==1) dui-=1;
        f.zd--;
    }//炸弹的带牌问题
    dan+=dui;
    while (f.ld>0&&dan>0){
        f.ld--;
        dan--;
    }
    if (dan>=f.w) dan-=f.w; else f.w=0;
    for (k=3;k<=15;k++)
        f.l[k]=0;
    make(f,step+dan);
}/*
