    ans=1<<30;
}
void work(int x,int deep,int rest){
    if(x>14) return;
    if(deep>ans) return;
    if(rest==0){
        ans=min(ans,deep);
        return;
    }
    if(c[x]==0)
        work(x+1,deep,rest);    
    for(int i=c[x];i>0;i--)
        Do(x,i,deep,rest);
    return;     
}
void Do(int x,int nu,int deep,int rest){ //出nu张x 
    if(nu==4){
        c[x]-=4;
        work(x,deep+1,rest-4);//炸 
        for(int i=x+1;i<=14;i++)//四带二 
            if(c[i]){
                c[i]--;
                for(int j=x+1;j<=14;j++)
                    if(c[j]){
                        c[j]--;
                        work(x,deep+1,rest-6);
                        c[j]++;
                    }
                c[i]++;
            }
        for(int i=x+1;i<=14;i++)//四带两对
            if(c[i]>1){
                c[i]-=2;
                for(int j=x+1;j<=14;j++)
                    if(c[j]>1){
                        c[j]-=2;
                        work(x,deep+1,rest-8);
                        c[j]+=2;
                    }
                c[i]+=2;
            }
        c[x]+=4;
        return;
    }
    if(nu==3){
        c[x]-=3;
        if(x>2)
            for(int i=x+1;i<=14;i++)//三顺子 
                if(c[i]<3) 
                    break;
                else
                    Sz(x,i,3,deep,rest);
        for(int i=x+1;i<=14;i++)//三带二 
            if(c[i]>1){
                c[i]-=2;
                work(x,deep+1,rest-5);
                c[i]+=2;
            }
        for(int i=x+1;i<=14;i++)//三带一 
            if(c[i]){
                c[i]--;
                work(x,deep+1,rest-4);
                c[i]++;
            }
        work(x,deep+1,rest-3);//出裸的三张
        c[x]+=3;
        return;
    }
    if(nu==2){
        c[x]-=2;
        if(x>2&&c[x+1]>1&&c[x+2]>1){ //可以构成双顺子 
            for(int i=x+2;i<=14;i++)
                if(c[i]<2) 
                    break;
                else
                    Sz(x,i,2,deep,rest);//双顺子 
        }
        for(int i=x+1;i<=14;i++){
            if(c[i]>3){
                c[i]-=4;
                work(x,deep+1,rest-6);//二带四
                for(int j=x+1;j<=14;j++) //二带四带二 
                    if(c[j]>1){
                        c[j]-=2;
                        work(x,deep+1,rest-8);
                        c[j]+=2;
                    }
                c[i]+=4;
            }
            if(c[i]>2){//二带三 
                c[i]-=3;
                work(x,deep+1,rest-5);
                c[i]+=3;
            }
        }
        work(x,deep+1,rest-2);//对子
        c[x]+=2;
        return;
    } 
    if(nu==1){
        c[x]-=1;
        if(x>2&&x<11&&c[x+1]&&c[x+2]&&c[x+3]&&c[x+4]){//可以构成单顺         
            for(int i=x+4;i<=14;i++)
                if(c[i]<1)
                    break;
                else
                    Sz(x,i,1,deep,rest);
        }
        for(int i=x+1;i<=14;i++)
            if(c[i]>3){//一带四加一 
                c[i]=0;
                for(int j=x+1;j<=14;j++)
                    if(c[j]){
                        c[j]--;
                        work(x,deep+1,rest-6);
                        c[j]++;
                    }
                c[i]=4;
            } 
        for(int i=x+1;i<=14;i++)
            if(c[i]>2){//一带三 
                c[i]-=3;
                work(x,deep+1,rest-4);
                c[i]+=3;
            }
        work(x,deep+1,rest-1);//单牌
        c[x]+=1;
        return;
    }
}
void Sz(int x,int y,int nu,int deep,int rest){//从x到y出nu顺子 
    for(int i=x+1;i<=y;i++)//之所以i从x+1开始是因为x的张数在Do函数里面减过了 
        c[i]-=nu;
    work(x,deep+1,rest-(y-x+1)*nu);
    for(int i=x+1;i<=y;i++)
        c[i]+=nu;
    return;
}

```

其实不需要四维数组dp，只用dfs用一些小技巧就行了。

1.搜索顺序很重要，我用的是：

**3顺 -> 2顺 -> 1顺 -> 4带2 -> 3带1**

2.单出某种牌根本不需要考虑dfs搜,我们可以在dfs的过程中直接考虑 “接下来**每种牌都单独出**最少出几次” 的问题。因为能带的出完了，剩下的只能单独出了。

3.再加一个最简单的剪枝，如果出到现在已经出了大于ans次，明显不用考虑了。

4.对于大小王，他们的性质是一样的，因此不用特判，甚至可以视为同一种卡牌。

最后，我还专门写了一个getstra函数来处理顺子问题，大大减小代码量！

A,2的下标分别为14 15.

```cpp
#include<iostream>
#include<cstdio>
#include<iomanip>
#define FOR(i,a,b) for(int i=a;i<=b;i++)
using namespace std;
int n,T,va,su,c[20],now,ans;
void dfs();//now表示现在出了几次
void getstra(int x,int y){
	FOR(i,3,14-y+1){
		int cnt=0;
		FOR(j,i,14)
			if(c[j]>=x) cnt++;else break;
		if(cnt>=y){
			FOR(j,i,i+cnt-1) c[j]-=x;dfs();
			FOR(j,i,i+cnt-1) c[j]+=x;
		}
	}
}
void dfs(){
	int cnt=0,tot=0;
	FOR(i,3,15) cnt+=(c[i]+3)/4;
	cnt+=(c[16]+1)/2;//是先看是否每种单出的结果
	if(now>=ans) return;
	ans=min(ans,cnt+now);
	now++;
	getstra(3,2),getstra(2,3),getstra(1,5);//顺子
	FOR(i,3,15){
		if(c[i]>=4){
			c[i]-=4;
			FOR(j,3,16)FOR(k,3,16){
				if((j-i&i-k&k-j)&&c[j]==c[k]&&c[j]){
					c[j]--,c[k]--,dfs();
					if(c[j]&&c[k])
						c[j]--,c[k]--,dfs(),c[j]++,c[k]++;
					c[j]++,c[k]++;
				}
			}c[i]+=4;
		}//处理4带
	}FOR(i,3,15){
		if(c[i]>=3){
			c[i]-=3;
			FOR(j,3,16)if((j-i)&&c[j]){
				c[j]--,dfs();
				if(c[j]) c[j]--,dfs(),c[j]++;
				c[j]++;
			}c[i]+=3; 
		}//处理3带
	}now--;
}
int main(){
	scanf("%d%d",&T,&n);
	while(T--){
		ans=n;
		FOR(i,3,16) c[i]=0; 
		FOR(i,1,n){
			scanf("%d%d",&va,&su);
			if(va==0) va=16;
			else if(va<3) va+=13;
			c[va]++;
		}dfs();
		printf("%d\n",ans);
	}
}
