    cin>>x;
    do 
    {
        i++;
        s=(1+i)*i/2;    //等差数列求和求规则的项数
    } while(s<x);
m=(x-s)*(i);//m是刨去规则的以外的零头的和
for(int k=1;k<=i;k++)
{
    sum=sum+pow(k,2);//用循环，叠加1^2,2^2,3^2一直到i^2,这些都是规则的
}
total=sum+m;//总数total等于前面的规则的数列加上零头
cout<<total;//输出total
}
