int main()
{
        scanf("%d",&n);
        for(int i=floor(sqrt(2*n));i>=1;i--)//找到已经给完的最大的金币数p 
        if(i*(i+1)/2<=n)
        {
            p=i;
            break;
        }
        sum=p*(p+1)*(2*p+1)/6+(n-(p*(p+1)/2))*(p+1);//上公式 求出前p*(p+1)/2天的金币 剩下的天每天给(p+1)枚
        printf("%d",sum);
        return 0; 
}
