}
using namespace io ;

using namespace std ;

#define N 100010
const int mod = 10007 ;

int n , m ;
struct node {
	int col , val ;
} a[ N ] ;
int cnt[ N ][ 2 ] , sum[ N ][ 2 ] ;

signed main() {
	n = read() , m = read() ;
	for( int i = 1 ; i <= n ; i ++ ) a[ i ].val = read() ;
	for( int i = 1 ; i <= n ; i ++ ) a[ i ].col = read() ;
	for( int i = 1 ; i <= n ; i ++ ) {
		cnt[ a[ i ].col ][ i&1 ] ++ ;
		sum[ a[ i ].col ][ i&1 ] = ( sum[ a[ i ].col ][ i&1 ] + a[ i ].val ) % mod ;
	}
	ll ans = 0 ;
	for( int i = 1 ; i <= n ; i ++ ) {
		ll num = cnt[ a[ i ].col ][ i&1 ] , s = sum[ a[ i ].col ][ i&1 ] ;
		ans = ( ans + ( i*(s-a[i].val) + (num-1)*a[i].val*i ) % mod ) % mod ;
	}
	printf( "%lld\n" , ans % mod ) ;
	return 0 ;
}

