```cpp
#include<iostream>
#include<cstring>
#include<cstdio>
#include<cmath>
#include<algorithm>
#define ll long long
#define INF 0x7fffffff
#define re register
#define mod 10007

using namespace std;

int read()
{
    register int x = 0,f = 1;register char ch;
    ch = getchar();
    while(ch > '9' || ch < '0'){if(ch == '-') f = -f;ch = getchar();}
    while(ch <= '9' && ch >= '0'){x = x * 10 + ch - 48;ch = getchar();}
    return x * f;
}

struct number{
	ll num,col,x;
}a[100005];

ll n,m,s,ans;

int mysort(number a, number b){
	if(a.col != b.col) return a.col < b.col;
	if(a.num % 2 == b.num % 2) return a.num < b.num;
	return a.num % 2 < b.num % 2;
}

int main()
{
	n = read(); m = read();
	for(re int i = 1; i <= n; i++) a[i].num = i;
	for(re int i = 1; i <= n; i++) a[i].x = read();
	for(re int i = 1; i <= n; i++) a[i].col = read();
	sort(a + 1, a + n + 1, mysort);
	int i = 1;
	while(i <= n){
		s = i;
		while(a[i].col == a[i + 1].col && a[i].num % 2 == a[i + 1].num % 2 && i < n) i ++;
		for(int j = s; j < i; j++)
			for(int k = j + 1; k <= i; k++)
				ans = (ans + (a[j].num + a[k].num) * (a[j].x + a[k].x)) % mod;
		i++;
	}
	printf("%lld\n",ans);
    return 0;
}

