```cpp
#include <iostream>
#include <algorithm>
using namespace std;

struct cow{
    int s,e;//结构体
};

cow a[25005];

bool cmp(cow c1,cow c2){ //结构体排序
    return c1.s<c2.s;
}

int main(){
    int n,t;
    cin >> n >> t;
    for (int i=0;i<n;i++) cin >> a[i].s >> a[i].e;//读入
    sort(a,a+n,cmp);//排序
    int e=0;
    int emax=0;
    int ans=0;
    int nn;
    bool have;
    for (int i=0;i<n;i++){//开扫
        have=false;
        for (int j=i;a[j].s<=e+1&&j<n;j++){
            have=true;
            if (a[j].e>=emax){

                emax=a[j].e;
                nn=j;
                i=j;
            }
        }
        if (have){//找到了，加一
            e=a[nn].e;
            ans++;
        }
        if (emax>=t){//结束已经超过了，直接结束
            cout << ans;
            return 0;
        }

        else if (!have){
            cout << -1;//没找到？输出负一
            return 0;
        }
    }
    cout << -1;//都扫完了肯定就输出-1了。
}
```
