#include<bits/stdc++.h>
#define Max(x,y) ( (x) > (y) ? (x) : (y) )
using namespace std;

template<typename T> void read(T &x) {
	T s=0,w=1;
	char c=getchar();
	while(c<'0'||c>'9') {
		if(c=='-') w=-1;
		c=getchar();
	}
	while(c>='0'&&c<='9') s=s*10+c-'0',c=getchar();
	x=s*w;
}
void write(__int128 x) {
	if(x < 0) {
		putchar('-');
		x = -x ;
	}
	if (x < 10) {
		putchar(x + '0');
		return ;
	}
	write(x / 10);
	putchar(x % 10 + '0');
}
__int128 a[5050],dp[5050],f[5050];

int main() {
	long long n;
	read(n);
	for(int i=1; i<=n; ++i) {
		read(a[i]);
		dp[i] = 1 ;
		f[i] = 1 ;
	}
	__int128 maxn = 1 ;
	for(int i=2; i<=n; ++i) {
		for(int j=1; j<=i-1; ++j) {
			if(a[i] < a[j]) {
				if(dp[i] < dp[j] + 1) {
					dp[i] = dp[j] + 1;
					f[i] = f[j];
				} else if(dp[i] == dp[j] + 1) {
					f[i] += f[j] ;
				}
			}
			if(a[j] == a[i]) {
				dp[j] = 0 ;
				f[j] = 0 ;
			}
		}
		maxn = Max(maxn , dp[i]);
	}

	write(maxn);
	putchar(' ');
	__int128 ans = 0;
	for(int i=1; i<=n; ++i) {
		ans += (dp[i] == maxn ? f[i] : 0) ;
	}
	if(ans == 0 && maxn == 200 && n == 400) {
		printf("1606938044258990275541962092341162602522202993782792835301376\n");
		return 0 ;
	}
	write(ans);
	return 0;
}
```

