    {
        cin>>e>>f;
        for(int p=e;p<=f;p++)
            a[p][1]=1;      //让已打扫的行面积变成1
    }

    for(int i=1;i<=g;i++)
    {
        cin>>k>>h;
        for(int q=k;q<=h;q++)
            a[1][q]=1;      //让已打扫列面积变成1
    }
    /*for(int i=1;i<=n;i++){
        for(int j=1;j<=m;j++)cout<<a[i][j]<<" ";cout<<endl;
    }*/
    for(int i=1;i<=n;i++)
    {
        if(a[i][1]==1)
        {
            sum+=m;
        }
    }
    for(int j=1;j<=m;j++)
    {
        if(a[1][j]==1)
        {
            sum+=n;
            for(int d=0;d<=n;d++)       //去掉重复的面积
            {
                if(a[d][1]==1)
                {
                    sum--;
                }
            }
