#include <bits/stdc++.h>
using namespace std;

const int N = 59;

int t,n;

struct node {
	int x,y;
}a[N];

bool cmp(const node &u,const node &v) {
	return u.y <= v.y;
}

void solve() {
	scanf("%d",&n);
	for(int i = 1;i <= n;i++) {
		scanf("%d %d",&a[i].x,&a[i].y);
	}
	for(int i = 0;i < n;i++) {
		for(int j = i + 1;j <= n;j++) {
			if(abs(a[i].x - a[j].x) > abs(a[i].y - a[j].y)) {
				puts("Notabletocatch");
				return;
			}
		}
	}
	puts("Abletocatch");
}

int main() {
	scanf("%d",&t);
	while(t--) solve();
	return 0;
} 
