#include<bits/stdc++.h>
using namespace std;
char s[1000005];
int ans,l;
void dfs(int cha/*abs(g-r)*/,int Nnum,int Nans) { //表示当前G-R的值，现在的编号（序号），现在的答案
	if(Nnum>l) return;//现在的序号大于长度return
