		dfs(cha-1,Nnum+1,Nans+1); //R大了1，差值减小1
	else dfs(cha+1,Nnum+1,Nans+1);//G大了1，差值增加1
	return;
}
int main() {
	gets(s);
	l=strlen(s);
	for(int i=0;i<l-ans;i++) //如果想要更新答案，当前的位置一定要在L-ans之前否则是找不到的
	dfs(0,i,0);
	cout<<ans<<endl;
	return 0;
}
