使用两个指针从两边开始扫 如果当前区间满足条件跳出

如果不满足 哪一种多删哪一种

由于存在一种情况两边都不是多的一种 那么就存在两种决策 所以到处两个答案取最优即可

```cpp
#include <iostream>
#include <cstring>
#include <cstdio>
#include <cmath>
using namespace std;
char a[1000005 * 2];
int sum1[1000005 * 2], sum2[1000005 * 2];
int main()
{
    scanf("%s", a);
    int len = strlen(a);
    for (int i=1; i<=len; i++) 
        if (a[i-1] == 'G') sum1[i] = sum1[i-1] + 1, sum2[i] = sum2[i-1];
        else sum1[i] = sum1[i-1], sum2[i] = sum2[i-1] + 1;
    int l = 1, r = len;
    while(l <= r)
    {
        int k1 = sum1[r] - sum1[l-1], k2 = sum2[r] - sum2[l-1];
        if (k1 == k2) break;
        else if (k1 > k2) 
        {
            if (a[l-1] == 'G') l++;
            else if (a[r-1] == 'G') r--;
            else r--;
        }
        else if (k1 < k2)
        {
            if (a[l-1] == 'R') l++;
            else if (a[r-1] == 'R') r--;
            else r--;
        }
    }
    int ans = r - l + 1;
    l = 1, r = len;
    while (l <= r)
    {
        int k1 = sum1[r] - sum1[l-1], k2 = sum2[r] - sum2[l-1];
        if (k1 == k2) break;
        else if (k1 > k2) 
        {
            if (a[l-1] == 'G') l++;
            else if (a[r-1] == 'G') r--;
            else l++;
        }
        else if (k1 < k2)
        {
            if (a[l-1] == 'R') l++;
            else if (a[r-1] == 'R') r--;
            else l++;
        }
    }
    ans = max(ans, r - l + 1);
    printf("%d\n", ans);
    return 0;
}
