#include <cstdio>
#include <algorithm>

using namespace std;

int main(void) {
    int r, b, c, d, e, ans = -0x7fffffff;
    scanf("%d %d %d %d %d", &r, &b, &c, &d, &e);
    for (int k = 0; k <= min(r, b); k++) {
        ans = max(ans, (r-k)*c + (b-k)*d + 2*k*e);
    }
    printf("%d", ans);
    return 0;
}
