    for(int i=1;i<=m;i++)//处理（平方）
    {
        while(l<a[i].x)
        {
            ans-=mp[b[l]]*mp[b[l]];
            mp[b[l]]--;
            ans+=mp[b[l]]*mp[b[l]];
            l++;
