            l--;
            ans-=mp[b[l]]*mp[b[l]];
            mp[b[l]]++;
            ans+=mp[b[l]]*mp[b[l]];
        }
        while(r>a[i].y)
        {
            ans-=mp[b[r]]*mp[b[r]];
            mp[b[r]]--;
            ans+=mp[b[r]]*mp[b[r]];
            r--;
        }
        while(r<a[i].y)
        {
            r++;
            ans-=mp[b[r]]*mp[b[r]];
            mp[b[r]]++;
            ans+=mp[b[r]]*mp[b[r]];
        }
        a[a[i].id].ans=ans;//按id来给答案
    }
    for(int i=1;i<=m;i++)//输出
    {
