#define ll long long
const int MAXN=1e5+5;
int n,k;
int a[MAXN];
ll s[MAXN];
ll ans=0;
void nxd(int l,int r){
	if(l==r) return;
	int mid=l+r>>1;
	nxd(l,mid),nxd(mid+1,r);
	int k=l-1;
	for(int i=mid+1;i<=r;i++){
		while(s[k+1]<=s[i]&&k<mid)
			k++;
		ans+=(k-l+1);
	}
	sort(s+l,s+r+1);
	return;
}
int main(){
	cin>>n>>k;
	for(int i=1;i<=n;i++){
		cin>>a[i];
		s[i]=s[i-1]+a[i]-k;
	}
	nxd(0,n);
	cout<<ans<<endl;
	return 0;
} 
