    int l2=strlen(s2+1);
    for(int i=0;i<=l1;i++)//初始化
       f[i][0]=i;
    for(int i=0;i<=l2;i++)//初始化
       f[0][i]=i;
    for(int i=1;i<=l1;i++)
      for(int j=1;j<=l2;j++)
        {
         if(s1[i]==s2[j])
           f[i][j]=f[i-1][j-1];
         else
           f[i][j]=min(f[i-1][j-1],min(f[i-1][j],f[i][j-1]))+1;//注意最后的+1
        }
