#include <bits/stdc++.h>
using namespace std;
long long f(int x){
	return x * log(x) / log(10) + 1;
}
int main(){
	long long N;
	cin >> N;
	long long l = 1,r = 2e9;
	while (l < r){
		long long mid = (l + r) / 2;
		if(f(mid) >= N) r = mid;
		else l = mid + 1;
	}
