# 代码
```cpp
#include <cstdio>

#define i32 int
#define i64 long long
#define u32 unsigned i32
#define u64 unsigned i64

const i64 MOD = 1234567891;
int T, N, K;

const int MAXK = 30;
struct Matrix
{
	i64 Mat[MAXK + 5][MAXK + 5];
	int R, C;

	Matrix()
	{
		for (int i = 1; i <= MAXK + 1; i++)
			for (int j = 1; j <= MAXK + 1; j++)
				Mat[i][j] = 0;
	}

	Matrix operator * (const Matrix One) const // 重载矩阵的乘号 
	{
		Matrix Res;
		Res.R = R, Res.C = C;
		for (int i = 1; i <= Res.R; i++) 
			for (int j = 1; j <= Res.C; j++)
				for (int k = 1; k <= One.R; k++)
					Res.Mat[i][j] = (Res.Mat[i][j] + Mat[i][k] * One.Mat[k][j]) % MOD;
		return Res;
	}
}A, B;

Matrix Pow(Matrix One, i64 k) // 矩阵快速幂 
{
	Matrix Res, cnt = One;
	Res.R = K + 1, Res.C = K + 1;
	for (int i = 1; i <= K + 1; i++)
		Res.Mat[i][i] = 1; // 单位矩阵 
	for (int i = k; i >= 1; i >>= 1)
	{
		if (i & 1) Res = Res * cnt;
		cnt = cnt * cnt;
	}
	return Res;
}

void Init(int k) // 初始化 原始矩阵 和 加速矩阵 
{
	A.R = 1, A.C = k + 1;
	A.Mat[1][1] = 1 * k;

	B.R = B.C = k + 1;
	B.Mat[1][1] = 1;
	for (int i = 2; i <= k; i++)
	{
		B.Mat[i][i] = i;
		B.Mat[i - 1][i] = k + 1 - i;
	}
	B.Mat[k][k + 1] = B.Mat[k + 1][k + 1] = 1;
}

int main()
{
	scanf("%d", &T);
	while (T--)
	{
		scanf("%d %d", &N, &K);
		Init(K);
		Matrix ans = A * Pow(B, N);
		printf("%lld\n", ans.Mat[1][K + 1]);
	}
	return 0;
}

