#include<bits/stdc++.h>
using namespace std;
#define MAXN 205
#define int long long
const int mod=1234567891;
int n,T,k;
struct Matrix{
	int c[MAXN][MAXN],n,m;
	Matrix operator *(const Matrix &x)const{
		Matrix r;
		r.n=n,r.m=x.m;
		for(int i=1;i<=n;i++)
			for(int j=1;j<=x.m;j++){
				int sum=0;
				for(int k=1;k<=m;k++)
					sum=(sum+c[i][k]*x.c[k][j])%mod;
				r.c[i][j]=sum;
			}
		return r;
	} 
}A,B;
Matrix qkpow(Matrix A,int n){
	Matrix res=A;
	while(n){
		if(n&1) res=res*A;
		A=A*A,n>>=1;
	}
	return res;
}
signed main(){
	scanf("%lld",&T);
	while(T--){
		scanf("%lld%lld",&n,&k);
		A.n=1,A.m=B.n=B.m=k+1;
		A.c[1][1]=k,B.c[1][1]=1;
		for(int i=2;i<=k;i++) B.c[i][i]=i,B.c[i-1][i]=k-i+1;
		B.c[k][k+1]=B.c[k+1][k+1]=1;
		Matrix ans=A*qkpow(B,n-1);
		printf("%lld\n",ans.c[1][k+1]);
	}
	return 0;
}
