#include <algorithm>
#include <cstdio>
#include <cstring>
using namespace std;
const int maxn=300005;
int n,num[maxn],ans=0,temp;
int find(int x){
    for(int i=x+1;i<=n;i++)
        if(num[i]>num[x]) return i;
    return 0;
}
int main(){
    scanf("%d",&n);
    for(int i=1;i<=n;i++)
        scanf("%d",&num[i]);
    sort(num+1,num+n+1);
    for(int i=1;i<=n;i++){
        if((num[i]+n)>=(num[n]+1))
            if(temp=find(i)){
                if((num[temp]+n-temp+1)<=num[i]+n) ans++;
            }
            else ans++;
    }
    printf("%d",ans);
    return 0;
}
