#include<bits/stdc++.h>
using namespace std;
int a[30001];
char b[30001];
int len,ans;
int main()
{
	for(len=1;;len=-~len)//这一部分是用来读入的
	{
		scanf("%d%c",&a[len],&b[len]);
		if(b[len]!='+' && b[len]!='-')
			break;
	}
	ans=a[1];
	for(int i=2;i<=len;i=-~i)//这一部分是用来计算的
	{
		if(b[i-1]=='-')
			ans-=a[i];
		else
			ans+=a[i];
	}
	printf("%d",ans);//输出
	return 0;
}
```

