string s;
void dfs(string s1)//深搜函数
{
	if(s1.length()%2==1)//如果字符串长度为奇数
	{
		cout<<s1.length()<<endl;//输出字符串长度
		return ;//退出函数
	}
	int h=0,t=s1.length()-1;//头尾指针
	while(h<t)//当头小于尾
	{
		if(s1[h]!=s1[t])//如果头尾指针指向的字符不同
		{
			cout<<s1.length()<<endl;//输出字符串长度
			return ;//退出函数
		}
		h++;//头指针加
		t--;//尾指针减
	}
	dfs(s1.substr(0,s1.length()/2));//递归
}
int main()
{
	getline(cin,s);//整行读入，比较快
	dfs(s);//开始搜索
	return 0;
}
