```pascal
program Tower;
var a,dp,df:array[0..10000] of longint;
      n,i:longint;
function min(x,y:longint):longint;
begin
    if x<y then exit(x)
        else exit(y);
end;
begin
    readln(n);
    for i:=1 to n do
        read(a[i]);
    df[1]:=a[1];
    for i:=2 to n do
        begin
            dp[i]:=min(df[i-1],df[i-2]);
            df[i]:=min(dp[i-1]+a[i],df[i-1]+a[i]);
        end;
    writeln(min(dp[n],df[n]));
end.
