		{
		    jihuishu++;
		}
		else if(a[jihuishu] < b[i]) // 直接攻击，记录击毁数。
		{
			jihuishu++;
			b[i] = 0;
		}	
	}
	if(jihuishu <= n) // 没打过，输出0。
	{
	    cout << "0" << endl;
	}
	else
	{
		for(int i = 1; i <= m; i++) //否则输出攻击系统剩余的值(也是对母舰的伤害值)。
