```cpp
#include <algorithm>
#include <iostream>
#include <cstring>
#include <cstdio>
#define LL long long
using namespace std;

const int Mod = 1e9 + 7;
const int Maxk = 10;
LL n,k,ans;
int pi[Maxk];

inline LL read(LL &AC)
{
    char ch=' ';LL x=0,f=1;
    for(; (ch!='-') && ((ch<'0')||(ch>'9')); ch=getchar());
    if(ch=='-') f=-1,ch=getchar();
    for(; ch>='0' && ch<='9'; ch=getchar()) x=x*10+ch-48;
    AC=x*f;    return AC;
}

inline LL ksm(LL a,LL p) {
    LL ret = 1;
    a%=Mod;
    for(; p; p>>=1, a=a*a%Mod)
        if(p&1)
            ret=ret*a%Mod;
    return ret;
}

inline bool check() {
    bool vis[Maxk];
    memset(vis,false,sizeof(vis));
    int now=1;
    while(!vis[now]) {
        vis[now]=true;
        now=pi[now];
    }
    ///从 1 出发必须能够回到 1  
    if(now!=1)
        return false;
    for(int i=1; i<=k; ++i) {
        if(!vis[i]) {
            bool vis2[Maxk];
            memset(vis2,false,sizeof(vis2));
            int now2=i;
            while(!vis2[now2] && !vis[now2]) {
                vis2[now2]=true;
                now2=pi[now2];
            }
            if(!vis[now2])
                return false;
        }
    }
    return true;
}

void dfs(int now) {
    if(now==k+1) {
        if(check())
            ans++;
        return ;
    }
    for(int i=1; i<=k; ++i) {
        pi[now]=i;
        dfs(now+1);
    }
}

int main() {
    read(n),read(k);
    dfs(1);
    ans=ans*ksm(n-k,n-k)%Mod;
    printf("%lld",ans);
    return 0;
}
```

