```cpp
#include <iostream>
#include <cstdio>
int t,k,n,m,ans;
using namespace std;
bool work(int a,int b)
{
    if(a==b||a==0) return 1%k==0?1:0;
    int p=1,now=2;
    for(int i=b-a+1;i<=b;i++)
    {
        p=p*i;
        while(p%now==0&&now<=a)
        {
            p=p/now;
            now++;
        }
    }
    if(p%k==0) return 1;
    else return 0;
}
int main()
{
    scanf("%d%d",&t,&k);
    while(t--)
    {
        ans=0;
        scanf("%d%d",&n,&m);
        for(int i=1;i<=n;i++)
        {
            if(i<=m) 
            {
                for(int j=1;j<i/2;j++)
                {
                    if(work(j,i)) ans+=2;
                }
                if(i%2==0)
                {
                    if(work(i/2,i)) ans+=1;
                }
            }
            else if(i/2<m)
            {
                for(int j=1;j<i/2;j++)
                {
                    if(work(j,i))
                    {
                        ans++;
                        if(i-j<=m) ans++;
                    }
                }
                if(i%2==0)
                {
                    if(work(i/2,i)) ans++;
                }
            }
            else 
            {
                for(int j=1;j<=m;j++)
                {
                    if(work(j,i)) ans+=1;
                }
            }
        }
        printf("%d\n",ans);
    }
    return 0;
}

