         begin
                s[i][j]:=s[i-1][j]+s[i][j-1]-s[i-1][j-1];
                if j<=i then
                if f[i][j]=0 then inc(s[i][j]);
         end;      
        for i:=1 to t do
        begin
                read(n,m);
                writeln(s[n][m]);
