考场上打出来的，跟很多人的算法貌似都不太一样，但是数据过了，大概意思就是先将K分解质因数，然后递推出C（n，m）与C（n，m+1），C（n+1，m）的关系，然后算出每一个组合数包含K的质因数的个数，暴力判断是否符合要求，然后求一个求前缀和就可以了

```cpp
#include<cstdio>
#include<cstring>
#include<iostream>
using namespace std;
int pr[10],cnt,sumk[10];
struct node
{
    int a[10];
    node(){memset(a,0,sizeof(a));}
    void print()
    {
        for(int i=1;i<=cnt;i++)
            printf("%d ",a[i]);
        puts("");
    }
    void add(int x)
    {
        for(int i=1;i<=cnt;i++)
        while(x%pr[i]==0)
        {
            a[i]++;
            x/=pr[i];
            //printf("%d ",pr[i]);
        }
    }
    void del(int x)
    {
        for(int i=1;i<=cnt;i++)
        while(x%pr[i]==0)
        {
            a[i]--;
            x/=pr[i];
            //printf("%d ",pr[i]);
        }
    }
}c[2010][2010];
int sum[2010][2010],k,t;
int n,m;
void get_prime(int x)
{
    for(int i=2;i<=x;i++)
    if(x%i==0)
    {
        pr[++cnt]=i;
        sumk[cnt]=0;
        while(x%i==0)
        {
            sumk[cnt]++;
            x/=i;
        }
    }
}
int main()
{
    scanf("%d%d",&t,&k);
    get_prime(k);
    for(int i=2;i<=2000;i++)
    {
        c[i][1]=c[i-1][1];
        c[i][1].add(i);
        c[i][1].del(i-1);
        int flag=1;
        for(int j=1;j<=cnt;j++)
            if(c[i][1].a[j]<sumk[j])
            {
                flag=0;
                break;
            }
        sum[i][1]=flag;
    }
    for(int i=1;i<=2000;i++)
        for(int j=2;j<=i;j++)
        {
            c[i][j]=c[i][j-1];
            c[i][j].add(i-j+1);
            c[i][j].del(j);
            int flag=1;
            for(int k=1;k<=cnt;k++)
                if(c[i][j].a[k]<sumk[k])
                {
                    flag=0;
                    break;
                }
            sum[i][j]=sum[i][j-1]+flag;
        }
    while(t--)
    {
        scanf("%d%d",&n,&m);
        int ans=0;
        for(int i=1;i<=n;i++)
            ans+=sum[i][min(i,m)];
        printf("%d\n",ans);
    }
    return 0;
}
