void fenjie(int t)//把t分解质因数，prime记录质因数，num记录该质因数的个数。
{
    int i=1;
    while(t>1)
    {
        if(t%p[i]==0)
        {
            if(prime[prime[0]]==p[i]){num[prime[0]]++;t/=p[i];}
            else {prime[++prime[0]]=p[i],t/=p[i];num[prime[0]]=1;}
