#include <cmath>
#include <cstdio>
#include <cstdlib>
#include <iostream>
using namespace std;
long long ext_euc(long long a, long long b, long long &x, long long &y)
{
    if(b == 0) {
        x = 1;
        y = 0;
        return a;
    }
    long long n = ext_euc(b, a % b, x, y);
    long long t = x;
    x = y;
    y = t - (a / b) * y;
    return n;
}
inline bool one_ans(long long a, long long b, long long c, long long &x, long long &y, long long &gcd)
{
    // ax + by + c = 0 (a != 0 || b != 0)
    c = -c;
    gcd = ext_euc(a, b, x, y);
    if(c % gcd != 0)
        return false;
    long long k = c / gcd;
    x *= k;
    y *= k;
    return true;
}
