#include<iostream>
using namespace std;
int a[201][201];//偷懒不打链表
int b[201];//存每一个人的负责人
int n,x,y,tot;
int i;//开全局变量节省内存
int dfs(int j)
{
    if(b[j]==i)return 0;
    //若这人由自己负责，那么他负责的人之前也归自己负责，没必要走下去了
    b[j]=i;//改成自己负责
	for(int k=1;k<=a[j][0];k++)
	dfs(a[j][k]);//为间接被自己负责的负责
}
