//链式前向星建边过程 
{
	o[++tot].next = first[u] ; 
	o[tot].to = v ;
	first[u] = tot ;
}

void tarjan(int u)
//Tarjan本质上也是个dfs 
{
	int v = 0;

	dfn[u] = low[u] = ++_time;
	//初始化，都设为当前时间戳 
	judge[u] = 1;
	s.push(u);
