			//先遍历这个点 
			low[u] = min(low[u] , low[v]);
			//回溯时更新low 
		}

		else if (judge[v])
		//如果这个点在栈里面 
			low[u] = min(low[u] , dfn[v]);
			//更新low的值，注意，要用dfn[v]而不是low[v] 
		}

		if(dfn[u] == low[u])
		//强联通分量中的最小时间戳 
		{
			++ cnt;
			do
			//因为是要先去栈顶元素再比较，所以用了do-while循环 
			{
				v = s.top();
				s.pop();
				judge[v] = 0;
				//退栈 
				belong[v] = cnt;
				//属于第几块 
