const int MAXN = 200 + 6;
bool in[MAXN];
class SCC{
public:
	std::vector<int> G[MAXN],G2[MAXN],S;
	bool hash[MAXN] = {};
	int SCCNo[MAXN] = {},SCCCnt = 0;

	void dfs1(int u){
		if(hash[u]) return;
		hash[u] = true;
		for(int i = 0;i < G[u].size();i++) dfs1(G[u][i]);
		S.push_back(u);
	}
	void dfs2(int u){
		if(SCCNo[u]) return;
		SCCNo[u] = SCCCnt;
		for(int i = 0;i < G2[u].size();i++) dfs2(G2[u][i]);
	}
	void findSCC(int n){
		SCCCnt = 0;
		for(int i = 0;i < n;i++) dfs1(i);
		for(int i = n - 1;i >= 0;i--) if(!SCCNo[S[i]]){SCCCnt++;dfs2(S[i]);}
	}
};

int main(){
	SCC s;
	int n,u;scanf("%d",&n);
	for(int i = 0;i < n;i++){
		while(scanf("%d",&u) && u){
			s.G[i].push_back(u - 1);
			s.G2[u - 1].push_back(i);
		}
	}
	s.findSCC(n);

	for(int i = 1;i <= s.SCCCnt;i++) in[i] = true;
	for(int u = 0;u < n;u++){
		for(int i = 0;i < s.G[u].size();i++){
			int v = s.G[u][i];
			if(s.SCCNo[u] != s.SCCNo[v]) in[s.SCCNo[v]] = false;
		}
	}
	int ans = 0;
	for(int i = 1;i <= s.SCCCnt;i++) if(in[i]) ans++;
	if(s.SCCCnt == 1) ans = 0;
	printf("%d",ans);

	return 0;
}

