我算是搬运一下，提供另一个用 stack 的版本。

本题的基本思路就是先把两边设成无限高，一个一个进栈，只要再进去的比栈顶元素高就把水注满再把栈顶元素弹出，保证栈内的元素单调递减。

代码：
```cpp
#include<bits/stdc++.h>
using namespace std;
struct sb{
	long long kuan,gao;
}a[100010];
long long sum[100010],temp,minn=99999999;
stack<long long>q;
int main()
{
	long long n;
	cin>>n;
	a[0].gao=999999999;
	a[n+1].gao=999999999;
	for(long long i=1;i<=n;i++)
	{
		cin>>a[i].kuan>>a[i].gao;
		if(a[i].gao<minn)
		minn=a[i].gao,temp=i;
	}
	q.push(0);
	q.push(temp);
	long long now=0,l=temp-1,r=temp+1;
	for(long long i=1;i<=n;i++)
	{
		long long xx,yy,kuan1=0;
		if(a[l].gao<a[r].gao)
		{
			xx=a[l].gao;
			yy=a[l].kuan;
			while(!q.empty()&&a[l].gao>a[q.top()].gao)
			{
				a[q.top()].kuan+=kuan1;
				sum[q.top()]=now+a[q.top()].kuan;
				kuan1=a[q.top()].kuan;
				int hhh=q.top();q.pop();
				now+=(min(a[q.top()].gao,a[l].gao)-a[hhh].gao)*a[hhh].kuan;
			}
			a[l].kuan+=kuan1;
			q.push(l);
			l--;
		}
		else
		{
			xx=a[r].gao;
			yy=a[r].kuan;
			while(!q.empty()&&a[r].gao>a[q.top()].gao)
			{
				a[q.top()].kuan+=kuan1;
				sum[q.top()]=now+a[q.top()].kuan;
				kuan1=a[q.top()].kuan;
				int hhh=q.top();q.pop();
				now+=(min(a[q.top()].gao,a[r].gao)-a[hhh].gao)*a[hhh].kuan;
			}
			a[r].kuan+=kuan1;
			q.push(r);
			r++;
		}
	}
	for(long long i=1;i<=n;i++)
	cout<<sum[i]<<endl;
	return 0;
 } 
