    for(register map<int,int>::iterator it=p.begin();it!=p.end();++it){//map要用迭代器来遍历
        if(it->second>maxn){//如果出现新的最大值
            tmp=it->first;//记录其关键字
            maxn=it->second;//更新最大值
        }
    }
