int main(){return 0;}
#include<queue>
queue<long long>a,b;
int c,n,a1,a2,b1,b2,d1,d2;
struct solve{solve(){
	scanf("%d%d%d%d%d%d%d%d",&c,&n,&a1,&b1,&d1,&a2,&b2,&d2);
	a.push(c);b.push(c);
	for(int i=1;i<n;i++){
		long long ai=a1*a.front()/d1+b1,bi=a2*b.front()/d2+b2,maxn=min(ai,bi);
		a.push(maxn),b.push(maxn);if(ai==maxn)a.pop();if(bi==maxn)b.pop();
	}
	printf("%lld",a.back());
}}mal_solve;
