{
    scanf("%d%d",&m,&n);
    for(int i=1;i<=n;i++)scanf("%d",&a[i]);
    sort(a+1,a+n+1);
    ans=max(a[1],m-a[n]);
    for(int    i=2;i<=n;i++)ans=max(ans,(a[i]-a[i-1])/2);
