	}
	memset(f,0x3f,sizeof(f));
	f[res]=0;//初始状态
	for(i=1;i<=n;i++)
	{
		for(j=0;j<=m+res;j++)//保证足够大
		{
			k=0;
			while(++k)
			if((k*k-a[i]*a[i])<=j) 
			f[j]=min(f[j],f[j-(k*k-a[i]*a[i])]+(k-a[i])*(k-a[i]));
