                if(dp[i-1][j-k*k]!=1e9)
					dp[i][j]=min(dp[i][j],dp[i-1][j-k*k]+abs(a[i]-k)*abs(a[i]-k));
        }
    }
    if(dp[n][m]==1e9)printf("-1");
	else printf("%d",dp[n][m]);
