#include<queue>
#include<stack>
#define LL long long
#define N 100005
#define p 1000000007
using namespace std;
inline int read() {
	int s = 0,w = 1;
	char ch = getchar();
	while(ch < '0' || ch > '9') {
		if(ch == '-')
			w = -1;
		ch = getchar();
	}
	while(ch >= '0' && ch <= '9') {
		s = s * 10 + ch - '0';
		ch = getchar();
	}
	return s * w;
}
int to[2 * N],nxt[2 * N],head[2 * N],vis[N],tot;
int tmp,res;
LL ans = 1;
void add(int u,int vis) {
	to[++tot] = vis;
	nxt[tot] = head[u];
	head[u] = tot;
	return ;
}
void dfs(int x) {
	vis[x] = 1;
	tmp++;
	for(int i = head[x]; i; i = nxt[i],res++)
		if(!vis[to[i]])
			dfs(to[i]);
}
int n,m;
void init() {
	n = read(),m = read();
	for(int i = 1; i <= m; i++) {
		int x = read(),y = read();
		add(x,y);
		add(y,x);
	}
}
void solve() {
	for(int i = 1; i <= n; i++) {
		if(!vis[i]) {
			tmp = res = 0;
			dfs(i);
			if(tmp > res / 2) 
				ans = ans * tmp % p;
			else 
				if(tmp == res / 2) 
					ans = ans * 2 % p;
			else ans = 0;
		}
	}
	printf("%lld",ans);
}
int main(){
	init();
	solve();
	return 0;
}
