#include<cstdio>
#include<iostream>
#include<algorithm>
using namespace std;
long long n,ans;
long long x[100003],y[100003],sum[100003];
int main(){
    cin>>n;
    for(int i=1;i<=n;i++){
        cin>>x[i]>>y[i];
        sum[i]=x[i-1]-y[i-1]+sum[i-1];
    }
    sum[1]=x[n]-y[n]+sum[n];
    sort(sum+1,sum+n+1);
    for(int i=1;i<=n;i++){
        ans+=abs(sum[i]-sum[n/2+1]);
    }
    cout<<ans<<endl;
    return 0;
}
