struct node
{
	int from,to,next;
	int h;
}edge[maxn];
bool cmp(node n1,node n2)
{
	return n1.h<n2.h;
}
int get_pos(int i,int j)
{
	return (i-1)*n+j;
}
int find(int x)
{
	if (fa[x]==x)
	{
		return x;
	}
	else
	{
		return fa[x]=find(fa[x]);
	}
}
signed main()
{
	cin>>m>>n>>t;
	for (int i=1;i<=m*n;i++)
	{
		fa[i]=i;
		cnt[i]=1;
	}
	int tot=0;
	for (int i=1;i<=m;i++)
	{
		for (int j=1;j<=n;j++)
		{
			cin>>x;
			a[get_pos(i,j)]=x;
			if (i!=m)
			{
				tot++;
				edge[tot].from=get_pos(i,j);
				edge[tot].to=get_pos(i+1,j);
			}	
			if (j!=n)
			{
				tot++;
				edge[tot].from=get_pos(i,j);
				edge[tot].to=get_pos(i,j+1);
			}
		}
	}

	for (int i=1;i<=tot;i++)
	{
		int from,to;
		from=edge[i].from,to=edge[i].to;
		int dif;
		dif=abs(a[from]-a[to]);
		edge[i].h=dif;
	}
	sort(edge+1,edge+tot+1,cmp);
	for (int i=1;i<=m;i++)
	{
		for (int j=1;j<=n;j++)
		{
			cin>>st_pt[get_pos(i,j)];
		}
	}
	int ans=0;
	for (int i=1;i<=tot;i++)
	{
		int fx=find(edge[i].from);
		int fy=find(edge[i].to);
		if (fx!=fy)
		{
			fa[fx]=fy;
			st_pt[fy]+=st_pt[fx];
			st_pt[fx]=0;
			cnt[fy]+=cnt[fx];
			cnt[fx]=0;
			if (cnt[fy]>=t)
			{
				ans+=edge[i].h*st_pt[fy];
				st_pt[fy]=0;
			}
		}
	}
	cout<<ans<<endl;
	return 0;
}
