                q[top].x=U(i,j);q[top].y=U(i,j+1);    
            }
        }
    sort(q+1,q+top+1,cmp);
    for(int i=1;i<=n*m;i++){
        size[i]=1;
        head[i]=i;
        last[i]=i;
    }
    for(int i=1;i<=top;i++){
        int X=get(q[i].x);
        int Y=get(q[i].y);
        if(X==Y)continue;
        f[Y]=X;size[X]+=size[Y];
        if(head[Y])
            if(!head[X]){
